function shape = get_shape(shape_id)

if strcmp(shape_id,'rect1')
        a = 0.09/2.0;
        b = 0.09/2.0;
        shape = [[a,b];[-a,b];[-a,-b];[a,-b];[a,b]];
elseif strcmp(shape_id,'rect2')
        a = 0.08991/2.0;
        b = 0.11258/2.0;
        shape = [[a,b];[-a,b];[-a,-b];[a,-b]; [a,b]];
elseif strcmp(shape_id,'rect3')       
