        b = 0.08994/2.0;
        shape = [[a,b];[-a,b];[-a,-b];[a,-b]; [a,b]];
elseif strcmp(shape_id,'tri1') 
        a = 0.12587;
        b = 0.12590;
        c = 0.178;
        d = 0.090/2.0;  % from the rectangle coordinate system
        shape = [[d, d]; [d-b,d]; [d,d-a]; [d,d]];
elseif strcmp(shape_id,'tri2')       
        a = 0.12587;
        b = 0.15100;
        c = 0.1962;
        d = 0.090/2.0;  % from the rectangle coordinate system
        shape = [[d, d]; [d-b,d]; [d,d-a]; [d,d]];
elseif strcmp(shape_id,'tri3')             
        a = 0.12561;
        b = 0.1765;
        c = 0.2152;
        d = 0.090/2.0;  % from the rectangle coordinate system
        shape = [[d, d]; [d-b,d]; [d,d-a]; [d,d]];
elseif strcmp(shape_id,'ellip1') 
        gamma = linspace(0,2*pi);
        shape = [0.105/2, 0.105/2];
        shape = [shape(1)*cos(gamma'), shape(2)*sin(gamma')];
elseif strcmp(shape_id,'ellip2')
        gamma = linspace(0,2*pi);
        shape = [0.105/2, 0.13089/2];
        shape = [shape(1)*cos(gamma'), shape(2)*sin(gamma')];
elseif strcmp(shape_id,'ellip3')             
        gamma = linspace(0,2*pi);    
        shape = [0.105/2, 0.157/2];
        shape = [shape(1)*cos(gamma'), shape(2)*sin(gamma')];
elseif strcmp(shape_id,'hex')      
        shape = [];
        for i = 1:6
            theta = (pi/3)*i;
            shape = [shape; 0.06050*[cos(theta), sin(theta)]];
        end
        shape = [shape; shape(1,:)];
